import{_ as e,o as l,c as i,e as t}from"./app.acfc35a6.js";const o={},_=t('<h1 id="谈谈你对导航守卫的了解" tabindex="-1"><a class="header-anchor" href="#谈谈你对导航守卫的了解" aria-hidden="true">#</a> 谈谈你对导航守卫的了解?</h1><ul><li>希望在页面加载之前，加载之后执行一些统一逻辑的话，可以使用导航守卫</li><li>导航守卫可以理解为就是 beforeEach 和 afterEach 两个方法</li><li>关于 beforeEach 和 afterEach 几个参数说明 <ul><li>to: Route: 即将要进入的目标 路由对象</li><li>from: Route: 当前导航正要离开的路由</li><li>next: Function: 一定要调用该方法来 resolve 这个钩子。执行效果依赖 next 方法的调用参数。</li></ul></li><li>实际应用场景 <ul><li>添加 NProgress 进度条</li><li>对即将访问的路径进行权限校验 <ul><li>是否有 token，没有 token 不给进</li><li>如果有 token，在判断当前用户角色，是否包含在路由的权限列表内，没有包含，表示该角色没有权限访问此路劲，那么就清空 token，然后跳登录页</li></ul></li><li>页面的标题动态更新</li></ul></li></ul>',2),a=[_];function r(c,n){return l(),i("div",null,a)}const u=e(o,[["render",r],["__file","02.谈谈你对导航守卫的了解.html.vue"]]);export{u as default};
